import React from "react";

function TextForms(props) {
  return (
    <>
      <div className="mb-3">
        <label for="myBox" className="form-label">
          <h1>{props.heading}</h1>
        </label>
        <textarea
          className="form-control"
          id="myBox"
          rows="3"
        ></textarea>
      </div>
    </>
  );
}

export default TextForms;
