import React, { useState } from 'react';

function TextForms(props) {
   const handleUpText=()=>{
        // console.log("handleUpText");
        let newText=text.toUpperCase();
        setText(newText)
    }
    const handleChangeText=(event)=>{
        // console.log("ON Change");
        setText(event.target.value)
    }
    const [text, setText] = useState(' ');
  return (
    <>
    <div className="container">
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea className="form-control" id="myBox" rows="3"  value={text}  onChange={handleChangeText}></textarea>
        <button type="button" className="btn btn-primary my-2" onClick={handleUpText}>Convert to UpperCase</button>
      </div>
      </div>
      <div className="container my-3">
        <h1>Your Text Summary</h1>
        <p>words <b>{text.split(" ").length-1}</b> and character <b>{text.length}</b></p>
        <p>{(0.008*text.split(" ").length)} In Mintues Read</p>
      </div>
    </>
  );
}

export default TextForms;
