import { useState } from "react";
import "./App.css";
import About from "./components/About";
import Navbar from "./components/Navbar";
import TextForms from "./components/TextForms";

function App() {
 const [modeTextColor,setmodeTextColor]=useState('dark')
  const [mode,setMode]=useState('light')

  const toggleMode=()=>{
    if(mode==='light')
    {
      setMode('dark')
      setmodeTextColor('light')
      document.body.style.backgroundColor='grey'
    }
    else{
      setMode('light')
      setmodeTextColor('dark')
      document.body.style.backgroundColor='white'
    }
  }
  // const name="Rahul Rathore"
  return (
    <>
      <Navbar title="TextUtils" aboutText="About us" mode={mode}  toggleMode={toggleMode}  modeTextColor={modeTextColor}></Navbar>
      <div className="container my-3">
        <TextForms heading="Enter the text to Analyse" mode={mode} />
        <About/>
      </div>
    </>
  );
}

export default App;
