import React from "react";

function TextForms(props) {
  return (
    <>
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea className="form-control" id="myBox" rows="3"></textarea>
        <button type="button" className="btn btn-primary my-2">Click here to UPPERCASE</button>
      </div>
    </>
  );
}

export default TextForms;
