import React, { useState } from 'react';

function TextForms(props) {
    handleUpText=()=>{
        console.log("handleUpText");
    }
    const [text, setText] = useState('Enter your text here');
  return (
    <>
      <h1>{props.heading}-{text}</h1>
      <div className="mb-3">
        <textarea className="form-control" id="myBox" rows="3"></textarea>
        <button type="button" className="btn btn-primary my-2" onClick={handleUpText}>Click here to UPPERCASE</button>
      </div>
    </>
  );
}

export default TextForms;
