        setText("RAHUL RATHORE")
    }
    const handleChangeText=(event)=>{
        console.log("ON Change");
        setText(event.target.value)
    }
    const [text, setText] = useState('');
  return (
    <>
      <h1>{props.heading}-{text}</h1>
      <div className="mb-3">
        <textarea className="form-control" id="myBox" rows="3"  value={text}  onChange={handleChangeText}></textarea>
        <button type="button" className="btn btn-primary my-2" onClick={handleUpText}>Click here to UPPERCASE</button>
      </div>
    </>
  );
}

